import React, {useState, useContext, useRef, useEffect} from 'react'
import {DataContext} from './Data'

export default function FormInput() {
  const [todos, setTodos] = useContext(DataContext);
  const [todoName, setTodoName] = useState('');
  const [todoTime, setTodoTime] = useState( new Date());
  const todoInput = useRef();
  const addTodo = e =>{
    e.preventDefault();
    const timeValue = todoTime.getHours()
    const dayValue = todoTime.getDate()
    const monthValue = todoTime.getMonth() + 1
    const yearValue = todoTime.getFullYear()
    const dateValue = timeValue + ":" + dayValue + "/" + monthValue + "/" + yearValue
    console.log(dateValue) 
    setTodos([...todos,{name: todoName, complete: false}])
    setTodoName('');
    todoInput.current.focus();
  }
  useEffect(() =>{
    todoInput.current.focus();
  },[])

  return (
    <form autoComplete = "off" onSubmit ={addTodo}> 
        <input type="text" name="todo" id="todo" ref={todoInput}
        required placeholder="What do you want to do today..." value={todoName} 
        onChange ={e => setTodoName(e.target.value.toLowerCase())}/>

        <button type="submit" className="submitCreate">Add</button>

    </form>
  )
}
